function calculateOrientation(lat1, lon1, lat2, lon2) {
    const R = 6371000; // Earth's radius in meters
    // Convert latitude and longitude to radians
    const toRadians = (degrees) => degrees * Math.PI / 180;
    const toDegrees = (radians) => radians * 180 / Math.PI;
    lat1 = toRadians(lat1);
    lon1 = toRadians(lon1);
    lat2 = toRadians(lat2);
    lon2 = toRadians(lon2);
    // Calculate differences
    const dlon = lon2 - lon1;
    // Bearing (Yaw) calculation
    const y = Math.sin(dlon) * Math.cos(lat2);
    const x = Math.cos(lat1) * Math.sin(lat2) - Math.sin(lat1) * Math.cos(lat2) * Math.cos(dlon);
    let bearing = Math.atan2(y, x);
    bearing = (toDegrees(bearing) + 360) % 360; // Convert to degrees and normalize
    // Distance calculation
    const a = Math.sin((lat2 - lat1) / 2) ** 2 + Math.cos(lat1) * Math.cos(lat2) * Math.sin(dlon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c;
    // Pitch calculation
    // Assuming searchlight is 2 meters above ground
    const searchlightHeight = 2;
    const pitch = -toDegrees(Math.atan2(searchlightHeight, distance));
    return { yaw: bearing, pitch: pitch };
}

