    const toDegrees = (radians) => radians * 180 / Math.PI;
    lat1 = toRadians(lat1);
    lon1 = toRadians(lon1);
    lat2 = toRadians(lat2);
    lon2 = toRadians(lon2);
    // Calculate differences
    const dlon = lon2 - lon1;
